mapper.select:
SELECT ID, USER_ID, REGION_NAME, DOG_SPECIES, DOG_SIZE, TITLE, NICKNAME, CREATED_DATE, VIEW_COUNT, GATHERING_DATE FROM (SELECT rownum rn, ID, USER_ID, REGION_NAME, DOG_SPECIES, DOG_SIZE, TITLE, NICKNAME, CREATED_DATE, VIEW_COUNT, GATHERING_DATE FROM (SELECT post.id, post.user_id, region.region_name, member.nickname, species.dog_species, dog_size.dog_size, post.title, post.created_date, post.view_count, post.gathering_date FROM dog_gather_post_tb post, user_tb member, region_tb region, dog_species_tb species, dog_size_tb dog_size WHERE
post.id > 0 and post.deleted=0 and post.user_id = member.id and post.region_id = region.id and post.species_id = species.id and species.size_id = dog_size.id
ORDER BY post.id DESC ) WHERE rownum <= #{pageNum} * #{amount} ) WHERE rn > (#{pageNum} - 1) * #{amount}

mapper.insert:
insert into DOG_GATHER_POST_TB values(
SEQ_DOG_GATHER_POST_TB_IDX.nextval, #{TITLE}, #{CONTENT}, 
#{GATHERING_ADDRESS}, #{GATHERING_DATE}, #{PEOPLE_COUNT}, sysdate, sysdate, 
#{VIEW_COUNT}, #{DELETED}, #{REGION_ID}, #{SPECIES_ID}, #{USER_ID}
)

mapper.update:
update DOG_GATHER_POST_TB
set 
	TITLE = #{TITLE}, CONTENT = #{CONTENT}, GATHERING_ADDRESS = #{GATHERING_ADDRESS},
	GATHERING_DATE = #{GATHERING_DATE}, PEOPLE_COUNT = #{PEOPLE_COUNT}, UPDATED_DATE = sysdate,
	REGION_ID = #{REGION_ID}, SPECIES_ID = #{SPECIES_ID} 
where ID = #{ID}
