mapper.select:

		SELECT ID, DOG_AGE, DOG_GENDER, DOG_LOST_ADDRESS, VIEW_COUNT, CREATE_DATE, UPDATED_DATE, FOUND, SPECIES_ID, DOG_SPECIES, NICKNAME
		FROM 
			(SELECT rownum rn, ID, DOG_AGE, DOG_GENDER, DOG_LOST_ADDRESS, VIEW_COUNT, CREATE_DATE, UPDATED_DATE, FOUND, SPECIES_ID, DOG_SPECIES, NICKNAME
    		FROM
    			(SELECT post.ID, post.DOG_AGE, post.DOG_GENDER, post.DOG_LOST_ADDRESS, post.VIEW_COUNT, post.CREATE_DATE, post.UPDATED_DATE, post.FOUND, post.SPECIES_ID, species.DOG_SPECIES, u.NICKNAME
				FROM dog_lost_post_tb post, dog_species_tb species, user_tb u
				WHERE post.DELETED=0 AND species.ID=post.SPECIES_ID AND u.ID=post.USER_ID
				ORDER BY post.FOUND ASC, post.CREATE_DATE DESC
				)
			WHERE	




			rownum <= #{pageNum} * #{amount}
			)		
		WHERE rn > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE dog_lost_post_tb
SET DELETED=1
WHERE ID=#{id}
